let inputsRequired=document.querySelectorAll("input[required]"),form=document.querySelector(".form");form.addEventListener("submit",(function(e){for(let t=0;t<inputsRequired.length;t++)inputsRequired[t].value&&!inputsRequired[t].validity.patternMismatch||(e.preventDefault(),inputsRequired[t].classList.add("form__input--error"))}));for(let e=0;e<inputsRequired.length;e++)inputsRequired[e].addEventListener("input",(function(){inputsRequired[e].classList.contains("form__input--error")&&""!==inputsRequired[e].value&&!inputsRequired[e].validity.patternMismatch&&inputsRequired[e].classList.remove("form__input--error")}));